// Generated by CoffeeScript 1.7.1
var divEscapeContentElement, divSystemContentElement, processUserInput, socket;

divEscapeContentElement = function(message) {
  return $('<div></div>').text(message);
};

divSystemContentElement = function(message) {
  return $('<div></div>').html('<i>' + message + '</i>');
};

processUserInput = function(chatApp, socket) {
  var message, systemMessage, systemmessage;
  message = $('#send-message').val();
  systemmessage = '';
  if (message.charAt(0) === '/') {
    systemMessage = chatApp.processCommand(message);
    if (systemMessage) {
      $('#messages').append(divSystemContentElement(systemMessage));
    }
  } else {
    chatApp.sendMessage($('#room').text(), message);
    $('#messages').append(divSystemContentElement(message));
    $('#messages').scrollTop($('#messages').prop('scrollHeight'));
  }
  return $('#send-message').val('');
};

socket = io.connect();

$(document).ready(function() {
  var chatApp;
  chatApp = new Chat(socket);
  socket.on('nameResult', function(result) {
    var message;
    message = '';
    if (result.success) {
      message = 'You are known as ' + result.name + '.';
    } else {
      message = result.message;
    }
    $('#messages').append(divSystemContentElement(message));
  });
  socket.on('joinResult', function(result) {
    $('#room').text(result.room);
    $('#messages').append(divSystemContentElement('Room changed.'));
  });
  socket.on('message', function(message) {
    var newElement;
    newElement = $('<div></div>').text(message.text);
    return $('#messages').append(newElement);
  });
  socket.on('rooms', function(rooms) {
    var room;
    $('#room-list').empty();
    for (room in rooms) {
      room = room.substring(1, room.length);
      if (room !== '') {
        $('#room-list').append(divEscapeContentElement(room));
      }
    }
    $('#room-list div').on('click', function() {
      chatApp.processCommand('/join ' + $(this).text());
      $('#send-message').focus();
    });
  });
  setInterval(function() {
    socket.emit('rooms');
    return true;
  }, 1000);
  $('#send-message').focus();
  $('#send-form').on('submit', function() {
    processUserInput(chatApp, socket);
    return false;
  });
  return true;
});
